                HealBot_UpdateUsedMedia("font")
            end
        else
            g=_G[self:GetName().."Text"]
            g:SetText(self.text);
        end       
        if val>0 then
            updatingMedia=false;
            HealBot_Timers_Set("SKINS","SetSkinBars")
        end
    end
end

