        g=_G["HealBot_Options_CopyFrame"..x]
        if g:GetChecked() then
            for y=1,21 do
                h=_G["HealBot_Options_CopyTab"..y]
                if h:GetChecked() then
                    HealBot_Options_CopyTab2Frames(hbCopyFrameIdx[x], y)
